        /// <summary>
        /// Returns the floor number that instructions lead Santa to
        /// </summary>
        /// <param name="input">Instructions to follow</param>
        /// <returns></returns>
        private static int floorNumber(string input)
        {
            int floor = 0;
            foreach (char c in input)
            {
                switch (c)
                {
                    case '(':
                        floor++;
                        break;
                    case ')':
                        floor--;
                        break;
                    default:
                        break;
                }
            }
            return floor;
        }
        /// <summary>
        /// Returns the index of 1st character that leads Santa to the Target floor
        /// </summary>
        /// <param name="input">Instructions to follow</param>
        /// <param name="floor">Target floor</param>
        /// <param name="index">Starting floor number</param>
        /// <returns></returns>
        private static int floorIndex(string input, int floor, ref int index)
        {
            int floorNumber = 0;
            foreach (char c in input)
            {
                index++;
                switch (c)
                {
                    case '(':
                        floorNumber++;
                        if (floorNumber == floor)
                            return index;
                        break;
                    case ')':
                        floorNumber--;
                        if (floorNumber == floor)
                            return index;
                        break;
                    default:
                        break;
                }
            }
            return index;
        }
